{*---------SET WELCOME CONTENT---------*}
{welcome_headline = "Thanks for signing up for Sailthru in Black & White"}
{welcome_message = "Click here to get started with our guide of helpful tips and tricks to get the most out of our site."}
{hero_image = "http://email-media.s3.amazonaws.com/Sailthru/default/images/welcome_deck.jpg"}
{target_url = "http://www.sailthru.com/?welcome_email"}
{mainButtonC2A = "GET STARTED"}
{showButton = "on"} {*set to "on" to display call to action button*}

{*----GLOBAL CONTROLS ----*}
{manual_subject_line = "This is a manually-added subject line"} {*add subject line text here*}
{manual_preheader = "This is a manually-added preheader text"} {*add preheader text here*}
{newsletter_name = "Sailthru Store in Black & White"} {*this is the title of your newsletter*}

{* ----- FOOTER SOCIAL ----- *}
{footersocial_switch = "on"} {*on or off to display*}
{footersocial_shading = "off"} {* #hex_code or "off" *}
{footersocial_borders = "off"} {* #hex_code or "off" *}
{footersocial_borders_height = "5"} {* number only *}
{footersocial_text = "Follow"}
{footersocial_text_color = "#000001"}{* #hex_code *}
{footersocial_btn_iconcolor = "white"} {* "white", "gray" or "black" *}
{footersocial_btn_bgcolor = "#25252B"} {* #hex_code, "default" or "off" *}
{footersocial_btn_rounded = "off"}{* "on" or "off" *}
{footersocial_btn_border = "off"}{* "black", "gray" or "off" *}

