{*PERSONALIZATION ALGORITHM SELECTION*}
{total_articles_number = heroSection_number + oneItemSmall_number + oneItemLarge_number + twoProductSection_number + threeProductSection_number + fourProductSection_number}

{if use_personalization == "on"}
  {content = personalize({"content":content,"algorithm":"interest","size":total_articles_number})}
{/if}

{*CONTENT SELECTION FOR SECTIONS/MODULES*}
{last = 0}
{* ----- HERO: 1-ITEM LARGE ----- *}
{if heroSection_number > 0}
  {total = heroSection_number}
  {start = 0}
  {heroSection_content = slice(content,start,total)}
  {last = heroSection_number}
{/if}

{* ----- PRODUCTS HORIZONTAL LARGE ----- *}
{if oneItemLarge_number > 0}
  {total = oneItemLarge_number}
  {start = last}
  {oneItemLarge_content = slice(content,start,total)}
  {last = last + oneItemLarge_number}
{/if}

{* ----- PRODUCTS HORIZONTAL SMALL ----- *}
{if oneItemSmall_number > 0}
  {total = oneItemSmall_number} 
  {start = last}
  {oneItemSmall_content = slice(content,start,total)}
  {last = last + oneItemSmall_number}
{/if}

{* ----- PRODUCT TILES: 2-item Row ----- *}
{if twoProductSection_number > 0}
  {total = twoProductSection_number}
  {start = last}
  {twoProductSection_content = slice(content,start,total)}
  {last = last + twoProductSection_number}
{/if}

{* ----- PRODUCT TILES: 3-item Row ----- *}
{if threeProductSection_number > 0}
  {total = threeProductSection_number}
  {start = last}
  {threeProductSection_content = slice(content,start,total)}
  {last = last + threeProductSection_number}
{/if}

{* ----- PRODUCT TILES: 4-item Row ----- *}
{if fourProductSection_number > 0}
  {total = fourProductSection_number}
  {start = last}
  {fourProductSection_content = slice(content,start,total)}
{/if}

{*-----SUBJECT LINE AND PREHEADER-----*}
{if length(manual_subject_line) > 5}
 {subject_line = manual_subject_line}
{else}
  {subject_line = content[0].title} {*default is title of first article*}
{/if} 

{if length(manual_preheader) > 5}
  {preheader_text = manual_preheader}
{else}
  {preheader_text = content[0].description} {*default is description of first article*}
{/if}

{*turns off section headers if products section is empty*}
{if total_articles_number < 1 || length(content) < 1}
  {display_section_header1 = "off"}
  {display_section_header2 = "off"}
  {display_section_header3 = "off"}
  {display_section_header4 = "off"}
  {display_section_header5 = "off"}
  {display_section_header6 = "off"}
{/if}
